// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "./interfaces/IOwnerContract.sol";

contract OwnerContract is IOwnerContract {
    address public owner;
    mapping(address => bool) public tokenSupport;
    address[] public supportedTokens;
    uint256 public totalTransactionVolume;
    uint256 public totalTransactionCount;
    uint256 public totalFeeCollected;
    mapping(address => uint256) public tokenFeesCollected;  // Tracks collected fees for each token
    uint256 public totalMerchantCount;
    uint256 public totalStoreCount;

    modifier onlyOwner() {
        require(msg.sender == owner, "Not authorized");
        _;
    }

    constructor() {
        owner = msg.sender;
        emit OwnershipTransferred(address(0), owner);
    }

    function transferOwnership(address newOwner) external onlyOwner {
        require(newOwner != address(0), "New owner is the zero address");
        emit OwnershipTransferred(owner, newOwner);
        owner = newOwner;
    }

    function removeTokens(address[] calldata tokens) external onlyOwner {
        for (uint256 i = 0; i < tokens.length; i++) {
            address token = tokens[i];
            require(tokenSupport[token], "Token not supported");
            tokenSupport[token] = false;

            uint256 lastTokenIndex = supportedTokens.length - 1;
            uint256 tokenIndex = findTokenIndex(token);
            supportedTokens[tokenIndex] = supportedTokens[lastTokenIndex];
            supportedTokens.pop();
            emit TokenRemoved(token);
        }
    }

    function findTokenIndex(address token) internal view returns (uint256) {
        for (uint256 i = 0; i < supportedTokens.length; i++) {
            if (supportedTokens[i] == token) {
                return i;
            }
        }
        revert("Token not found");
    }

    function getSupportedTokens() external view returns (address[] memory) {
        return supportedTokens;
    }
//volume and transaction
    function viewSystemTransactions() external view returns (uint256, uint256) {
        return (totalTransactionCount, totalTransactionVolume);
    }

    function incrementTransactionCount() external {
        totalTransactionCount += 1;
        emit TransactionCountIncremented(totalTransactionCount);
    }

     function addTransactionVolume(uint256 volumeUSDT) external {
        totalTransactionVolume += volumeUSDT;
        emit TransactionVolumeAdded(volumeUSDT, totalTransactionVolume);
    }

        function addTokens(address[] calldata tokens) external onlyOwner {
        for (uint256 i = 0; i < tokens.length; i++) {
            address token = tokens[i];
            require(!tokenSupport[token], "Token already supported");
            tokenSupport[token] = true;
            supportedTokens.push(token);
            emit TokenAdded(token);
        }
    }

// colected fee
    function collectFee(address token, uint256 amount, uint256 amountUSDT) external {
        totalFeeCollected = ( amountUSDT * 5 ) / 100;
        tokenFeesCollected[token] += amount;
        emit FeeCollected(token, amount);
    }


    function viewTotalFee() external view returns (uint256) {
        return totalFeeCollected;  
    }

// merchant and store

    function incrementMerchantCount() external {
        totalMerchantCount += 1;
        emit MerchantCountIncremented(totalMerchantCount);
    }

    function incrementStoreCount() external {
        totalStoreCount += 1;
        emit StoreCountIncremented(totalStoreCount);
    }

    function viewTotalMerchants() external view returns (uint256) {
        return totalMerchantCount;
    }

    function viewTotalStoreCount() external view returns (uint256) {
        return totalStoreCount;
    }



    function isTokenSupported(address token) external view returns (bool) {
        return tokenSupport[token];
    }
}

contracts/PaymentContract.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "./interfaces/IPaymentContract.sol";
import "./OwnerContract.sol";
import "./MerchantRegister.sol";
import "./StoreContract.sol";

contract PaymentContract is IPaymentContract {
    OwnerContract public ownerContract;
    MerchantRegister public merchantRegister;
    StoreContract public storeContract;

    uint256 public constant FEE_PERCENTAGE = 5;
    uint256 public constant BASIC_PLAN_TRANSACTION_LIMIT = 20;
    uint256 public constant BASIC_PLAN_VOLUME_LIMIT_USDT = 20;


    constructor(address _ownerContractAddress, address _merchantRegisterAddress, address _storeContractAddress) {
        ownerContract = OwnerContract(_ownerContractAddress);
        merchantRegister = MerchantRegister(_merchantRegisterAddress);
        storeContract = StoreContract(_storeContractAddress);
    }

    function processPayment(address merchant, uint256 storeId, address token, uint256 amount, uint256 usdtEquivalent) external payable override {
        (bool isRegistered, bool isPremium) = merchantRegister.merchantInfo(merchant);
        require(isRegistered, "Merchant not registered");
        require(storeContract.storeExists(merchant, storeId), "Store does not exist");
        require(isTokenAcceptedByStore(merchant, storeId, token), "Token not accepted by the store");

        (uint256 transactionCount, uint256 transactionVolume) = storeContract.viewStoreTransactions(merchant, storeId);

        if (!isPremium) {
            require(transactionCount < BASIC_PLAN_TRANSACTION_LIMIT, "Transaction count limit exceeded");
            require(transactionVolume <= BASIC_PLAN_VOLUME_LIMIT_USDT, "Volume limit exceeded");
        }

        uint256 fee = 0;
        uint256 merchantAmount = amount;

        if (isPremium) {
            fee = (amount * FEE_PERCENTAGE) / 100;
            merchantAmount = amount - fee;
        }

        address owner = ownerContract.owner(); 

        if (token == address(0)) {
            require(msg.value == amount, "Incorrect payment amount");
            payable(merchant).transfer(merchantAmount);
            if (fee > 0) {
                payable(owner).transfer(fee); 
            }
        } else {
            IERC20 paymentToken = IERC20(token);
            require(paymentToken.transferFrom(msg.sender, merchant, merchantAmount), "Token transfer to merchant failed");
            if (fee > 0) {
                require(paymentToken.transferFrom(msg.sender, owner, fee), "Token transfer of fee failed"); 
            }
        }

        updateTransactionRecords(merchant, storeId, token, usdtEquivalent, fee);

        emit PaymentProcessed(msg.sender, merchant, storeId, token, amount, fee, usdtEquivalent);
    }

    function isTokenAcceptedByStore(address merchant, uint256 storeId, address token) internal view returns (bool) {
        address[] memory acceptedTokens = storeContract.viewStoreTokenAccepted(merchant, storeId);
        for (uint256 i = 0; i < acceptedTokens.length; i++) {
            if (acceptedTokens[i] == token) {
                return true;
            }
        }
        return false;
    }

    function updateTransactionRecords(address merchant, uint256 storeId, address token, uint256 usdtEquivalent, uint256 fee) internal {
        ownerContract.incrementTransactionCount();
        ownerContract.addTransactionVolume(usdtEquivalent);
        ownerContract.collectFee(token, fee, usdtEquivalent);

        storeContract.incrementStoreTransactionCount(merchant, storeId);
        storeContract.addStoreTransactionVolume(merchant, storeId, usdtEquivalent);
    }
}

contracts/StoreContract.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "./interfaces/IStoreContract.sol";
import "./MerchantRegister.sol";
import "./OwnerContract.sol";

contract StoreContract is IStoreContract {
    OwnerContract public ownerContract;
    MerchantRegister public merchantRegister;

    struct Store {
        address[] acceptedTokens;
        uint256 transactionCount;
        uint256 transactionVolume;
    }

    mapping(address => mapping(uint256 => Store)) public stores;
    mapping(address => uint256) public storeCounts;

    event StoreCreated(address indexed merchant, uint256 storeId);

    constructor(address _ownerContractAddress, address _merchantRegisterAddress)
    {
        ownerContract = OwnerContract(_ownerContractAddress);
        merchantRegister = MerchantRegister(_merchantRegisterAddress);
    }

    function createStore(address[] calldata acceptedTokens) external override {
        (bool isRegistered, bool isPremium) = merchantRegister.merchantInfo(
            msg.sender
        );
        require(isRegistered, "Merchant not registered");

        if (!isPremium) {
            require(
                storeCounts[msg.sender] == 0,
                "Basic plan merchants can only create one store"
            );
        }

        for (uint256 i = 0; i < acceptedTokens.length; i++) {
            require(
                ownerContract.tokenSupport(acceptedTokens[i]),
                "Token not supported by owner"
            );
        }

        uint256 storeId = storeCounts[msg.sender];
        stores[msg.sender][storeId] = Store({
            acceptedTokens: acceptedTokens,
            transactionCount: 0,
            transactionVolume: 0
        });
        storeCounts[msg.sender]++;

        emit StoreCreated(msg.sender, storeId);
    }

    function viewStoreTokenAccepted(address merchant, uint256 storeId)
        external
        view
        override
        returns (address[] memory acceptedTokens)
    {
        return stores[merchant][storeId].acceptedTokens;
    }

    function viewStoreTransactions(address merchant, uint256 storeId)
        external
        view
        override
        returns (uint256 transactionCount, uint256 transactionVolume)
    {
        Store storage store = stores[merchant][storeId];
        return (store.transactionCount, store.transactionVolume);
    }

    function incrementStoreTransactionCount(address merchant, uint256 storeId)
        external
        override
    {
        stores[merchant][storeId].transactionCount++;
    }

    function addStoreTransactionVolume(
        address merchant,
        uint256 storeId,
        uint256 volumeUSDT
    ) external override {
        stores[merchant][storeId].transactionVolume += volumeUSDT;
    }

    function storeExists(address merchant, uint256 storeId)
        external
        view
        returns (bool)
    {
        return storeId < storeCounts[merchant];
    }
}

